<?phpif (!defined('BASEPATH'))    exit('No direct script access allowed');class Lib_rest {    public function rest_post($url, $data, $decode = FALSE)    {        $curl = curl_init($url);        curl_setopt($curl, CURLOPT_RETURNTRANSFER, true);        curl_setopt($curl, CURLOPT_POST, true);        curl_setopt($curl, CURLOPT_POSTFIELDS, $data);        $curl_response = curl_exec($curl);        curl_close($curl);        if ($decode)        {            return json_decode($curl_response);        }        else        {            return $curl_response;        }    }    public function rest_get($url, $decode = FALSE)    {        /* $content = file_get_contents($url); */        $ch = curl_init($url);        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);        $result = curl_exec($ch);        if ($decode)        {            return json_decode($result);        }        else        {            return $result;        }    }}